const mongoose = require('mongoose');
const Schema = mongoose.Schema;

const userSchema = new Schema(
  {
    username: {
      type: String,
      unique: true,
      required: true
    },
    password: {
      type: String,
      trim: true,
      required: true
    },

    profilePicture: {
      imgPath: {
        type: String,
        default:
          'data:image/jpeg;base64,'
      },
      imgName: {
        type: String,
        default: 'Profile Picture'
      }
    }
  },
  {timestamps: true}
);

module.exports = mongoose.model('User', userSchema);
